mapper.update:
update sys_user 
set
	password = #{password},
	update_date =#{updateTime}
where 
	user_id = #{userId}

mapper.select:
SELECT user_id AS userId, user_name AS userName, PASSWORD, description, is_system AS isSystem, company_id as companyId, icon, su.email, mobile_phone AS phone, gender
FROM sys_user su
where su.user_name=#{name}

mapper.update:
update sys_user
set
identify_code = #{identifyCode}, update_date = #{updateTime}
where
mobile_phone = #{phone}

update sys_user
set
password = #{password}, identify_code = NULL, update_date = #{updateTime}
where
mobile_phone = #{phone}
